public void Initialize()
{
    super();

    localContract = contract as OneMoreActionQueueMenuItemContract;
}

public void runActionInternal()
{
    try
    {
        OneMoreActionQueueContext::oneMoreActionQueueMenuItemContext = localContract;

        OneMoreActionQueueTypeMenuItemParameterTable oneMoreActionQueueTypeMenuItemParameterTable = OneMoreActionQueueTypeMenuItemParameterTable::find(oneMoreActionQueueTypeTable.TypeId);

        OneMoreActionQueueMenuItemBehavior menuItemBehavior = OneMoreActionQueueMenuItemBehavior::construct(oneMoreActionQueueTypeMenuItemParameterTable.ClassNumber);

        menuItemBehavior.initialize();

        if (!menuItemBehavior.unpackParameters(oneMoreActionQueueTypeMenuItemParameterTable.PackedParameters))
        {
            throw error("@OneMoreActionQueueFramework:OneMoreActionQueueCantUnpackMenuItemParameters");
        }

        menuItemBehavior.executeMenuItem();
    }
    finally
    {
        OneMoreActionQueueContext::oneMoreActionQueueMenuItemContext = null;
    }
}
