SourceCode.Declaration:
/// <summary>
/// Action queue contract for menu item actions
/// </summary>
[DataContract]
class OneMoreActionQueueMenuItemContract extends OneMoreActionQueueContract
{
    List menuItemParameters = new List(Types::Class);

}

Method.Source:
[
    DataMemberAttribute("MenuItemParameters"), 
    DataCollectionAttribute(Types::Class, classStr(OneMoreActionQueueMenuItemParameterContract))
]
public List parmMenuItemParameters(List _menuItemParameters = menuItemParameters)
{
    if (!prmIsDefault(_menuItemParameters))
    {
        menuItemParameters = _menuItemParameters;
    }

    return menuItemParameters;
}

/// <summary>
/// Add menu item parameter
/// </summary>
/// <param name = "parameterName">Parameter name</param>
/// <param name = "parameterValue">Parameter value</param>
public void addMenuItemParameter(str parameterName, str parameterValue)
{
    OneMoreActionQueueMenuItemParameterContract contract = new OneMoreActionQueueMenuItemParameterContract();

    contract.parmParameterName(parameterName);
    contract.parmParameterValue(parameterValue);

    menuItemParameters.addEnd(contract);
}
