SourceCode.Declaration:
/// <summary>
/// Action queue single record processing controller
/// </summary>
class OneMoreActionQueueProcessingSingleController extends SysOperationServiceController
{
}

Method.Source:
/// <summary>
/// Initialize contract
/// </summary>
/// <param name = "_args">Arguments</param>
protected void initContract(Args _args)
{
    OneMoreActionQueueProcessingSingleContract  contract = this.getDataContractObject('_contract');

    contract.parmActionQueueRecId(_args.record().RecId);
}

/// <summary>
/// Main
/// </summary>
/// <param name = "_args">Arguments</param>
public static void main(Args _args)
{
    OneMoreActionQueueProcessingSingleController   controller;
    FormDataSource                          fds;

    controller = OneMoreActionQueueProcessingSingleController::newFromArgs(_args);
    controller.startOperation();

    if (_args.record())
    {
        if (FormDataUtil::isFormDataSource(_args.record()))
        {
            fds = FormDataUtil::getFormDataSource(_args.record());
            fds.research(true);
        }
    }
}

/// <summary>
/// New from arguments
/// </summary>
/// <param name = "_args">Arguments</param>
/// <returns>New class</returns>
public static OneMoreActionQueueProcessingSingleController newFromArgs(Args _args)
{
    OneMoreActionQueueProcessingSingleController     ret;

    ret = new OneMoreActionQueueProcessingSingleController();

    ret.initializeFromArgs(_args);
    ret.parmClassName(classStr(OneMoreActionQueueProcessingService));
    ret.parmMethodName(methodStr(OneMoreActionQueueProcessingService, processActionQueueRecord));
    ret.parmExecutionMode(SysOperationExecutionMode::Synchronous);
    ret.initContract(_args);

    return ret;
}

/// <summary>
/// Can be overridden to inidicate that the operation supports running in a different, non interactive
/// session than the one from which it was launched
/// from the client
/// </summary>
/// <returns>If true operation will be run synchrnously in an non GUI async session</returns>
protected boolean canRunInNewSession()
{
    return true;
}
